mapper.select:
-- 返回类型是DiscussPost对象 不是java自带所以需要声明
        select  
        from discuss_post
        where status != 2   /*即不是拉黑状态*/
        /*即查看自己发布的帖子*/
            and user_id = #{userId}
        
        /*倒序排序 1为置顶就在前面   在类型相同情况下按时间排序这是权宜之计以后用热度排序*/
        order by type desc, create_time desc
        /*分页*/
        limit #{offset},#{limit}

/*因为可以自动识别*/
        select  count(id)
        from discuss_post
        where status != 2
        
            and user_id = #{userId}
